    def withdraw_all_collateral(self, collateral: AbstractTarotCollateral):
        amount = collateral.get_balance(self.w3.eth.default_account)
        tx_hash = self.withdraw_collateral(collateral, amount)
        return tx_hash

    def withdraw_collateral(self, collateral: TarotCollateralType, amount: int):
        """amount should include multiplication of DECIMALS"""
        deadline = get_deadline()
        tx_hash = self.contract.functions.redeem(
            poolToken=Web3.toChecksumAddress(collateral.ADDRESS), tokens=int(amount),
            to=self.w3.eth.default_account, deadline=deadline, permitData=b'').transact()
        return tx_hash

    def borrow(self, borrowable: TarotBorrowableType, amount: int, transact_config: TransactConfig = None):
        """amount should include multiplication of DECIMALS"""
        deadline = get_deadline()
        contract_func = self.contract.functions.borrow(
            borrowable=Web3.toChecksumAddress(borrowable.ADDRESS), amount=int(amount),
            to=self.w3.eth.default_account, deadline=deadline, permitData=b'')
        tx_hash = transact(contract_func, transact_config)
        return tx_hash
